import React, { useContext } from "react";
import { ShopContext } from "../../context/shop-context";
import CartItem from "./cart-item";
import CheckoutForm from "../checkoutform/CheckoutForm";
import { PRODUCTS } from "../../products";
import "./cart.css";

const Cart = () => {
  const { cartItems, getTotalCartAmount } = useContext(ShopContext);

  const cartItemsList = Object.entries(cartItems).map(([itemId, quantity]) => {
    const product = PRODUCTS.find((product) => product.id === Number(itemId));
    return (
      <CartItem
        key={itemId}
        product={product}
        quantity={quantity}
      />
    );
  });

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      <div className="cart-items">{cartItemsList}</div>
      <div className="cart-total">
        <h2>Total: ${getTotalCartAmount().toFixed(2)}</h2>
      </div>
      <CheckoutForm />
    </div>
  );
};

export default Cart;
